
import React, { useState, useCallback } from 'react';
import { initialContent } from './data';
import type { Section } from './types';
import Header from './components/Header';
import Hero from './components/Hero';
import Navigation from './components/Navigation';
import SectionCard from './components/SectionCard';
import AdminToggle from './components/AdminToggle';
import LoginModal from './components/LoginModal';
import MobileNavigation from './components/MobileNavigation';

const heroImage = 'data:image/jpeg;base64,-';

const App: React.FC = () => {
  const [content, setContent] = useState<Section[]>(initialContent);
  const [isEditing, setIsEditing] = useState(false);
  const [isLoginModalOpen, setIsLoginModalOpen] = useState(false);
  const [isMobileNavOpen, setIsMobileNavOpen] = useState(false);

  const handleContentChange = useCallback((sectionId: string, newTitle: string, newItems: string[]) => {
    setContent(prevContent =>
      prevContent.map(section =>
        section.id === sectionId
          ? { ...section, title: newTitle, items: newItems }
          : section
      )
    );
  }, []);
  
  const handleToggleEditMode = () => {
    if (isEditing) {
      setIsEditing(false); // Log out
    } else {
      setIsLoginModalOpen(true); // Show login modal to log in
    }
  };

  const handleLoginSuccess = () => {
    setIsEditing(true);
    setIsLoginModalOpen(false);
  };

  return (
    <div className="min-h-screen bg-slate-50 font-sans text-slate-800">
      <Header onMenuClick={() => setIsMobileNavOpen(true)} />
      <MobileNavigation 
        isOpen={isMobileNavOpen}
        onClose={() => setIsMobileNavOpen(false)}
        sections={content}
      />
      <div className="relative">
        <Hero 
          imageUrl={heroImage}
          title="Bienvenidos a Mar Azul" 
          subtitle="Esperamos que disfruten de una estadÃ­a tranquila y agradable."
        />
        <AdminToggle isEditing={isEditing} onToggle={handleToggleEditMode} />
      </div>

      <div className="container mx-auto max-w-7xl p-4 sm:p-6 lg:p-8">
        <div className="lg:grid lg:grid-cols-12 lg:gap-8">
          <aside className="hidden lg:block lg:col-span-3 lg:sticky top-8 self-start mb-8 lg:mb-0">
            <Navigation sections={content} />
          </aside>

          <main className="lg:col-span-9 space-y-8">
            {content.map(section => (
              <SectionCard
                key={section.id}
                section={section}
                isEditing={isEditing}
                onContentChange={handleContentChange}
              />
            ))}
             <footer className="text-center text-slate-500 py-8">
                <p>Esperamos que disfruten su estadÃ­a y se sientan como en casa.</p>
                <p className="text-2xl mt-2">ğŸ¡ ğŸ’§ ğŸ”¥ ğŸŒ²</p>
            </footer>
          </main>
        </div>
      </div>
      <LoginModal 
        isOpen={isLoginModalOpen}
        onClose={() => setIsLoginModalOpen(false)}
        onLoginSuccess={handleLoginSuccess}
      />
    </div>
  );
};

export default App;
